import React, { useEffect, useState } from 'react';
import './Home.css';
import Particles from 'react-tsparticles';
import { loadFull } from "tsparticles";
import Typist from 'react-typist';

const Home = () => {
    const particlesInit = async (main) => {
        console.log(main);

        // you can initialize the tsParticles instance (main) here, adding custom shapes or presets
        // this loads the tsparticles package bundle, it's the easiest method for getting everything ready
        // starting from v2 you can add only the features you need reducing the bundle size
        await loadFull(main);
    };

    const particlesLoaded = (container) => {
        console.log(container);
    };
    const [count, setCount] = useState(1);

    useEffect(() => {
        // document.title = `You clicked ${count} times`;
        console.log("Count: " + count);
        setCount(1);
    }, [count]);



    return (
        <div className="w-100 rox">
            <Particles className='particle'
                id="tsparticles"
                init={particlesInit}
                loaded={particlesLoaded}
                options={{

                    fpsLimit: 120,
                    interactivity: {
                        events: {
                            onClick: {
                                enable: true,
                                mode: "push",
                            },
                            onHover: {
                                enable: true,
                                mode: "repulse",
                            },
                            resize: true,
                        },
                        modes: {
                            push: {
                                quantity: 4,
                            },
                            repulse: {
                                distance: 200,
                                duration: 0.5,
                            },
                        },
                    },
                    particles: {
                        color: {
                            value: "#ffffff",
                        },
                        links: {
                            color: "#ffffff",
                            distance: 150,
                            enable: true,
                            opacity: 0.5,
                            width: 1,
                        },
                        collisions: {
                            enable: true,
                        },
                        move: {
                            direction: "none",
                            enable: true,
                            outModes: {
                                default: "bounce",
                            },
                            random: false,
                            speed: 2,
                            straight: false,
                        },
                        number: {
                            density: {
                                enable: true,
                                area: 1000,
                            },
                            value: 120,
                        },
                        opacity: {
                            value: 0.5,
                        },
                        shape: {
                            type: "circle",
                        },
                        size: {
                            value: { min: 1, max: 5 },
                        },
                    },
                    detectRetina: true,
                }}
            />

            <div className="container  container-fluid w-100 " id="particles-js" >


                <div className="row align-items-center ">

                    <div className="col text-style container fluid p-5">
                        <h4 className='intro '>Hello, my name is</h4>
                        <h1 className='name '>Nazmul Hasan Palash</h1>
                        <h3>And I'am
                            {count ? (
                                <Typist avgTypingDelay={50} onTypingDone={() => setCount(0)}>
                                    <span className="span-style">10</span>
                                    <Typist.Backspace count={20} delay={800} />
                                    <span className="span-style">20</span>
                                    <Typist.Backspace count={20} delay={800} />
                                    <span className="span-style">30</span>
                                    <Typist.Backspace count={20} delay={800} />
                                    <span className="span-style">40</span>
                                </Typist>
                            ) : (
                                ""
                            )}
                        </h3>
                    </div>
                    <div className="col">

                        <div className=" mx-auto w-100 profile align-items-center">
                            <img src="image/polash.png" className="card-img-top img-fluid my-5 w-75" alt="..." />

                        </div>

                    </div>
                </div>

            </div>


        </div>
    );
};

export default Home;
