import React from 'react';
import Particles from 'react-tsparticles';
import { loadFull } from "tsparticles";
import Typed from 'react-typed';
import Record from '../../component/Record/Record';
const Banner = () => {
    const particlesInit = async (main) => {
        console.log(main);

        // you can initialize the tsParticles instance (main) here, adding custom shapes or presets
        // this loads the tsparticles package bundle, it's the easiest method for getting everything ready
        // starting from v2 you can add only the features you need reducing the bundle size
        await loadFull(main);
    };

    const particlesLoaded = (container) => {
        console.log(container);
    };
    return (
        <div>
            <div className="w-100 rox">
                <Particles className='particle'
                    id="tsparticles"
                    init={particlesInit}
                    loaded={particlesLoaded}
                    options={{

                        fpsLimit: 120,
                        interactivity: {
                            events: {
                                onClick: {
                                    enable: true,
                                    mode: "push",
                                },
                                onHover: {
                                    enable: true,
                                    mode: "repulse",
                                },
                                resize: true,
                            },
                            modes: {
                                push: {
                                    quantity: 4,
                                },
                                repulse: {
                                    distance: 200,
                                    duration: 0.5,
                                },
                            },
                        },
                        particles: {
                            color: {
                                value: "#ffffff",
                            },
                            links: {
                                color: "#ffffff",
                                distance: 150,
                                enable: true,
                                opacity: 0.5,
                                width: 1,
                            },
                            collisions: {
                                enable: true,
                            },
                            move: {
                                direction: "none",
                                enable: true,
                                outModes: {
                                    default: "bounce",
                                },
                                random: false,
                                speed: 2,
                                straight: false,
                            },
                            number: {
                                density: {
                                    enable: true,
                                    area: 1000,
                                },
                                value: 120,
                            },
                            opacity: {
                                value: 0.5,
                            },
                            shape: {
                                type: "circle",
                            },
                            size: {
                                value: { min: 1, max: 5 },
                            },
                        },
                        detectRetina: true,
                    }}
                />

                <div className="container  container-fluid w-100 " id="particles-js" >


                    <div className="row align-items-center ">

                        <div className="col  container fluid ">
                            <div className='ms-5  text-style'>
                                <h5 className='intro '>Hello, my name is</h5>
                                <h2 className='name '> Mohammad Nazmul Hasan Palash</h2>
                                <h3 className='type'>
                                    And, I am <Typed strings={['Web Developer', 'Freelancer', 'Content Writer', 'Blogger']}
                                        loop={5}

                                        cursorStyle='|'
                                        typeSpeed={50}
                                        deleteSpeed={50}
                                        delaySpeed={1000}
                                        className='span-style'>

                                    </Typed>
                                </h3>
                                <button className="btn btn-danger btn-style me-3 my-3 contact-style"><a href="#contact">Contact
                                    me</a></button>
                                <button className="btn btn-warning btn-style me-3 resume-style"><a target={"_blank"}
                                    href="https://drive.google.com/file/d/1UBismI6E2wijWkBwwrLGgCw3uyegALL3/view?usp=sharing" rel="noreferrer">
                                    Download Resume</a>
                                </button>

                            </div>

                        </div>
                        <div className="col">

                            <div className=" mx-auto w-100 profile align-items-center">
                                <img src="image/polash.png" className="card-img-top img-fluid my-5 w-75" alt="..." />

                            </div>

                        </div>
                    </div>

                </div>


            </div>
        </div>
    );
};

export default Banner;
